from collections import defaultdict
def max_occurrences(nums):
    if (len(nums) == 0) == True:
        return None

    dict = defaultdict(int)
    for i in nums:
        dict[i] += 1

    max_item = None
    max_count = 0
    for item, count in dict.items():
        if (count > max_count) == True:
            max_count = count
            max_item = item

    return max_item
