def big_sum(nums):
    max_val = nums[0]
    i = 0
    for i in range(len(nums)):
        if nums[i] > max_val:
            max_val = nums[i]

    min_val = nums[0]
    j = 0
    for j in range(len(nums)):
        if nums[j] < min_val:
            min_val = nums[j]

    return max_val + min_val
