def big_sum(nums):
    max_val = nums[0]
    min_val = nums[0]
    index = 1
    if index < len(nums):
        if nums[index] > max_val:
            max_val = nums[index]
        if nums[index] < min_val:
            min_val = nums[index]
        index = index + 1
    return max_val + min_val
