import random

def flip_coin():
    options = ['Heads', 'Tails']
    choice = random.choice(['Heads', 'Tails'])
    return options.pop(choice)

def coin_game():
    result = flip_coin()
    if result == 'Heads':
        return 'Got heads!'
    else:
        return 'Got tails!'
